CREATE DATABASE sensores;
CREATE TABLE sensores (id int, nombre varchar(100), uso varchar(100), modelo int, cantidad int, error float);
CREATE TABLE encargados (id int, nombre varchar(30), apellido varchar(40), puesto varchar(50), edad int, ingreso int);
CREATE TABLE mediciones (id int, fecha datetime, id_estacion int, id_encargado int, id_sensor int);
CREATE TABLE estaciones (id int, nombre varchar(100), location varchar(100), latitud float, longitud float);





SELECT nombre, apellido FROM encargados;
SELECT nombre, apellido FROM encargados WHERE apellido = 'Stanton' ;
SELECT mediciones.fecha, encargados.nombre, encargados.apellido, FROM mediciones LEFT JOIN encargados ON mediciones.id_encargado = encargados.id;
SELECT estaciones.nombre , encargados.nombre , sensores.nombre, mediciones.medicion FROM mediciones, encargados, estaciones, sensores WHERE mediciones.id_estacion =   estacion.id AND mediciones.id_encargado = encargados.id AND mediciones.id_sensor = sensores.id;
